import React from "react";
import "../../styles/login.css";

import { authEndpoint, clientId, redirectUri, scopes } from "../lib/spotify";

const Login = () => {
  const handleClick = `${authEndpoint}?client_id=${clientId}&redirect_uri=${redirectUri}&scope=${scopes.join("%20")}&response_type=token&show_dialog=true`;

  return (
    <div className="login">
      <img
        src="https://storage.googleapis.com/pr-newsroom-wp/1/2018/11/Spotify_Logo_RGB_White.png"
        alt="the spotify logo"
      />
      <a href={handleClick}>LOGIN WITH SPOTIFY</a>
    </div>
  );
};

export default Login;
